<?php class pages extends masterclass{	function extend(){ 		$langs = DBall("SELECT * FROM langs");		$this->defmethod = 'view'; 		if($this->do == '' || !method_exists($this,$this->do)){  $this->do = 'view'; }		foreach ($langs as $lang){			$this->options['lang'][$lang['abbr']] = $lang['name'];		}		$this->tpl = $this->className = 'pages';		$this->sql = $this->sqlSettings();			}		function view(){ 		$sql = "SELECT * FROM pages WHERE lang = '".getVar('lang')."'"; 		$path = $this->path[0]; if($path[0] == 'pages') { unset($path[0]; }		$sql .= "AND url='".join('/',$path)."'";		$data = DBrow($sql);							return $data;	}			function items(){		unset($this->fields['text']);		$res = parent :: items();		foreach ($res as $k => $v) unset($res[$k]['text']);		return $res;	}		function tags() { 		include('metatags.php'); /*inspect($_METATAGS);*/		$this->title = T('tags');		return $_METATAGS;	}} ?>