<?php class pages extends masterclass{	function extend(){ 		$langs = DBall("SELECT * FROM langs");		$this->defmethod = 'view'; 		if($this->do == '' || !method_exists($this,$this->do)){  $this->do = 'view'; }		foreach ($langs as $lang){			$this->options['lang'][$lang['abbr']] = $lang['name'];		}		$this->tpl = $this->className = 'pages';		$this->sql = $this->sqlSettings();			}		function view(){ 		$sql = "SELECT * FROM pages WHERE lang = '".getVar('lang')."'"; 		if($this->path[0] !='pages') $sql .= "AND url='".join('/',$this->path)."'";		$data = DBrow($sql);							return $data;	}			function items(){		unset($this->fields['text']);		$res = parent :: items();		foreach ($res as $k => $v) unset($res[$k]['text']);		return $res;	}} ?>